import React, {useState,useEffect,useContext} from 'react';
import LedgerApi from '../Api/api';
import LoadingSpinner from '../common/LoadingSpinner'
import UserContext from '../auth/UserContext';
import ExpenseCardList from './ExpenseCardList';
import { useNavigate } from "react-router-dom";




function TotalExpenses(){

    const {currentUser, setCurrentUser} = useContext(UserContext);

    const history = useNavigate();

    const [expenses, setExpenses] = useState([]);

    useEffect(function getExpenses(){
        getUserExpenses(currentUser.user.username);
    },[expenses]);

    let result = [];
    async function getUserExpenses(username){
        result = await LedgerApi.getCurrentExpenses(username);
        setExpenses(result)
    }

    function handleSubmit (evt){
        evt.preventDefault();
        history(`/${currentUser.user.username}/edit`);
    }

    if(!expenses) return <LoadingSpinner />;

    return (
        <div>
            {expenses.length
                ?<ExpenseCardList expenses={expenses}/>
                :<p className="lead">Sorry, no results were found!</p>
            }
            <button onClick={handleSubmit}>Add New Expense</button>
        </div>
    )

}

export default TotalExpenses;
