import React, { Component,useState } from 'react'
import Chart from 'react-google-charts'


function LineChart({chartData,chartTitle}) {

  console.log(chartData)

  const [expense,setExpense]=useState(0);
  const[income,setincome]=useState(0);

  console.log(chartData[0])
  // chartData.map(c=>{
  //   if(c.date.getDate())
  // })

  const LineData = [
    ['x', 'expense', 'income'],
    [0,0,0],
    // [1, 10, 5],
    // [2, 23, 15],
    // [3, 17, 9],
    // [4, 18, 10],
    // [5, 9, 5],
    // [6, 11, 3],
    // [7, 27, 19],
    // [8,0,0],
    // [9,0,0],
    // [10,0,0],
    // [11,0,0],
    // [12,0,0],
    // [13,0,0],
    // [14,0,0],
    // [15,0,0],
    // [16,0,0],
    // [17,0,0],
    // [18,0,0],
    // [19,0,0],
    // [20,0,0],
    // [21,0,0],
    // [22,0,0],
    // [23,0,0],
    // [24,0,0],
    // [25,0,0],
    // [26,0,0],
    // [27,0,0],
    // [28,0,0],
    // [29,0,0],
    // [30,0,0],
    // [31,0,0]
  ]

  let date = 1;
  while(date <32){
    chartData.map(c=>{
      if(c.date.getDate() === date){
        if(c.amount>=0){setincome(income + c.amount)}
        if(c.amount < 0){setExpense(expense - c.amount)}
      }
    date ++  
    })
  }



    const LineChartOptions = {
      hAxis: {
        title: 'Date',
      },
      vAxis: {
        title: 'Expense/Income',
      },
    }

    return (
      <div className="container mt-5">
        <h2>{chartTitle}</h2>
        <Chart
          width={'700px'}
          height={'410px'}
          chartType="LineChart"
          loader={<div>Loading Chart</div>}
          data={LineData}
          options={LineChartOptions}
          rootProps={{ 'data-testid': '2' }}
        />
      </div>
    )
}

export default LineChart;
