import React, { Component,useState } from 'react'
import Chart from 'react-google-charts';
import LedgerApi from '../Api/api';


function LineChart({username,year,month,chartTitle}) {

  const [income,setIncome] = useState(0);
  const [expense,setExpense] = useState(0);

  const [lineData,setLineData] = useState([
    ['x', 'expense', 'income'],
    [0,0,0],
    // [1, 10, 5],
    // [2, 23, 15],
    // [3, 17, 9],
    // [4, 18, 10],
    // [5, 9, 5],
    // [6, 11, 3],
    // [7, 27, 19],
    // [8,0,0],
    // [9,0,0],
    // [10,0,0],
    // [11,0,0],
    // [12,0,0],
    // [13,0,0],
    // [14,0,0],
    // [15,0,0],
    // [16,0,0],
    // [17,0,0],
    // [18,0,0],
    // [19,0,0],
    // [20,0,0],
    // [21,0,0],
    // [22,0,0],
    // [23,0,0],
    // [24,0,0],
    // [25,0,0],
    // [26,0,0],
    // [27,0,0],
    // [28,0,0],
    // [29,0,0],
    // [30,0,0],
    // [31,0,0]
  ])

  async function getdailyData(username,year,month,day){
    let income = await LedgerApi.getIncomesByDay(username,year,month,day);
    console.log(income);
    setIncome(income);
    let expense = await LedgerApi.getExpensesByDay(username,year,month,day);
    console.log( expense);
    setExpense(expense);
  }

    const [date,setDate] = useState(1);
    while(date<32){
      getdailyData(username,year,month,date);
      console.log(date);
      setLineData(...lineData,[date,income,expense])
      setDate(date+1);
    }
  //   async function getUserExpenses(username){
  //     let result = await LedgerApi.getByMonth(username,year,month);
  //     console.log(result);
  //     setExpenses(result)
  // }
    const LineChartOptions = {
      hAxis: {
        title: 'Date',
      },
      vAxis: {
        title: 'Expense/Income',
      },
    }

    return (
      <div className="container mt-5">
        <h2>{chartTitle}</h2>
        <Chart
          width={'700px'}
          height={'410px'}
          chartType="LineChart"
          loader={<div>Loading Chart</div>}
          data={lineData}
          options={LineChartOptions}
          rootProps={{ 'data-testid': '2' }}
        />
      </div>
    )
}

export default LineChart;
