import React, {useState,useEffect,useContext} from 'react';
import LedgerApi from '../Api/api';
import LoadingSpinner from '../common/LoadingSpinner'
import UserContext from '../auth/UserContext';
import ExpenseCardList from './ExpenseCardList';
import { useNavigate,useParams } from "react-router-dom";




function MonthlyExpenses(){

    const {currentUser, setCurrentUser} = useContext(UserContext);
    let {year, month} = useParams()

    const history = useNavigate();

    const [expenses, setExpenses] = useState(null);

    const CurrentUser = currentUser.user.username;

    let nextM = month+1;
    let prevM = month-1;

    useEffect(function getExpenses(){
        if(year,month){
        getUserExpenses(CurrentUser);
        }
        if(year,nextM){
            async function nextMonth(){
            setExpenses( await LedgerApi.getExpenses(CurrentUser,year,nextM) );
            }
        }
        if(year,prevM){
            async function prevMonth(){
            setExpenses( await LedgerApi.getExpenses(CurrentUser,year,prevM) );
            }
        }
    },[]);

    async function getUserExpenses(username){
        let result = await LedgerApi.getByMonth(username,year,month);
        console.log(result);
        setExpenses(result)
    }

    function addExpense (evt){
        evt.preventDefault();
        history(`/expenses/add`);
    }

    function previousMonth (evt){
        let newYear;
        let newMonth;
        if(month ===1){
            newYear = year-1;
            newMonth = 12;
        }else{
            newYear = year;
            newMonth = month-1;
        }
        history(`/expenses/year/${newYear}/month/${newMonth}`);
    }

    function nextMonth (evt){
        let newYear;
        let newMonth;
        if(month ===12){
            newYear = year+1;
            newMonth = 1;
        }else{
            newYear = year;
            newMonth = Number(month) + 1;
        }
        history(`/expenses/year/${newYear}/month/${newMonth}`);
    }

    if(!expenses) return <LoadingSpinner />;

    return (
        <div>
            <div>
                {expenses.expenses.length
                    ?<ExpenseCardList expenses={expenses.expenses}/>
                    :<p className="lead">Sorry, no results were found!</p>
                }
                <button onClick={addExpense}>Add New Expense</button>
                <button onClick={previousMonth}>Previous Month Expenses</button>
                <button onClick={nextMonth}>Next Month Expenses</button>
            </div>

        </div>
    )

}

export default MonthlyExpenses;
