import React, {useState,useEffect,useContext} from 'react';
import LedgerApi from '../Api/api';
import LoadingSpinner from '../common/LoadingSpinner'
import UserContext from '../auth/UserContext';
import ExpenseCardList from './ExpenseCardList';
import { useNavigate,useParams } from "react-router-dom";




function MonthlyExpenses(){

    const {currentUser, setCurrentUser} = useContext(UserContext);

    const [formData, setFormData] = useState({
        date: "",
    });
    const [formErrors, setFormErrors] = useState([]);

    let {year, month} = useParams()

    const history = useNavigate();

    const [expenses, setExpenses] = useState(null);

    const CurrentUser = currentUser.user.username;

    useEffect(function getExpenses(){
        getUserExpenses(CurrentUser);
    },[]);

    async function getUserExpenses(username){
        let result = await LedgerApi.getByMonth(username,year,month);
        console.log(result,year,month);
        setExpenses(result)
    }


    /** Handle form data changing */
    function handleChange(evt) {
        const { name, value } = evt.target;
        setFormData(f => ({
            ...f,
            [name]: value,
        }));
        setFormErrors([]);
        }

    function addExpense (evt){
        evt.preventDefault();
        history(`/expenses/add`);
    }

    // function previousMonth (evt){
    //     let newYear;
    //     let newMonth;
    //     if(month ===1){
    //         newYear = year-1;
    //         newMonth = 12;
    //     }else{
    //         newYear = year;
    //         newMonth = month-1;
    //     }
    //     history(`/expenses/year/${newYear}/month/${newMonth}`,{replace: true});
    // }

    // function nextMonth (evt){
    //     let newYear;
    //     let newMonth;
    //     if(month ===12){
    //         newYear = year+1;
    //         newMonth = 1;
    //     }else{
    //         newYear = year;
    //         newMonth = Number(month) + 1;
    //     }
    //     history(`/expenses/year/${newYear}/month/${newMonth}`);
    // }

    function selectedMonth (evt){
        let newYear;
        let newMonth;
        let date = new Date(formData.date)
        newYear = date.getFullYear();
        newMonth = date.getMonth()+1;
        console.log(newMonth.newYear);
        history(`/expenses/year/${newYear}/month/${newMonth}`);
    }

    if(!expenses) return <LoadingSpinner />;

    return (
        <div>
            <div>
                {expenses.expenses.length
                    ?<ExpenseCardList expenses={expenses.expenses}/>
                    :<p className="lead">Sorry, no results were found!</p>
                }
                <button onClick={addExpense}>Add New Expense</button>
                {/* <button onClick={previousMonth}>Previous Month Expenses</button>
                <button onClick={nextMonth}>Next Month Expenses</button> */}
                <form onSubmit={selectedMonth}>
                <div className="form-group">
                  <label>Date</label>
                  <input
                    type="date"
                      name="date"
                      className="form-control"
                      value={formData.date}
                      onChange={handleChange}
                  />
                </div>
                <button onClick={selectedMonth}>Previous Month Expenses</button>
                </form>
            </div>

        </div>
    )

}

export default MonthlyExpenses;
