import React, {useState,useEffect,useContext}  from 'react';
import LedgerApi from '../Api/api';
import LoadingSpinner from '../common/LoadingSpinner';
import UserContext from '../auth/UserContext';
import { useNavigate } from "react-router-dom";
import PopupUser from '../common/PopDeleteUser';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';

function Profile(){
    const history = useNavigate();
    const {currentUser, setCurrentUser} = useContext(UserContext);
    const [popup, setPopup] = useState({
        show: false
      });
    function handleSubmit (evt){
        evt.preventDefault();
        history(`/${currentUser.user.username}/edit`);
    }

    function deleteUser (evt){
        evt.preventDefault();
        setPopup({show:true});
    }

    if(!currentUser.user.username){
        history(`/`);
    }

    return (
        <div>
            <div>
            Username:{currentUser.user.username}
            </div>
            <div>
            First Name:{currentUser.user.first_name}
            </div>
            <div>
            Last Name:{currentUser.user.last_name}
            </div>
            <div>
            Email:{currentUser.user.email}
            </div>
            <button onClick={handleSubmit}>Edit</button>
            <button onClick={deleteUser}>Delete User</button>
            {popup.show && <PopupUser popup={popup} setPopup={setPopup} />}
            <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            borderColor: 'white',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
          </Avatar>
          <Typography component="h1" variant="h5">
            Username: {formData.username}
          </Typography>
          <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <Grid container spacing={2}>
            {/* <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="username"
                  label="Username"
                  type="username"
                  id="username"
                  autoComplete="new-username"
                  value={formData.username}
                  onChange={handleChange}
                />
              </Grid> */}
              <Grid item xs={12} sm={6}>
                <TextField
                  autoComplete="given-name"
                  name="firstName"
                  required
                  fullWidth
                  id="firstName"
                  label="First Name"
                  autoFocus
                  value={formData.firstName}
                      onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  id="lastName"
                  label="Last Name"
                  name="lastName"
                  autoComplete="family-name"
                  value={formData.lastName}
                      onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name="email"
                  autoComplete="email"
                  value={formData.email}
                      onChange={handleChange}
                />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Save Changes
            </Button>
          </Box>
        </Box>
      </Container>
        </div>
    )

}

export default Profile;
