import React, {useState,useEffect,useContext} from 'react';
import LedgerApi from '../Api/api';
import LoadingSpinner from '../common/LoadingSpinner'
import SearchForm from "../common/SearchForm";
import UserContext from '../auth/UserContext';
import ExpenseCardList from './ExpenseCardList';



function TotalExpenses(){

    const {currentUser, setCurrentUser} = useContext(UserContext);


    const [expenses, setExpenses] = useState(null);

    useEffect(function getExpensesOnMount(){
        search(currentUser.user.username);
    },[]);

    async function search(username){
        let expenses = await LedgerApi.getCurrentExpenses(username);
        setExpenses(expenses);
    };

    if(!expenses) return <LoadingSpinner />;
    let newExpeneses = Array.from(expenses);

    return (
        <div>
            {/* <SearchForm searchFor={search} /> */}
            {!expenses.length
                ? (
                    <div>
                    {currentUser.user.username}

                    <ExpenseCardList newExpeneses />

                    </div>
                )
                :(
                    <p className="lead">Sorry, no results were found!</p>
                )}
        </div>
    )

}

export default TotalExpenses;
