import React, {useState,useEffect,useContext}  from 'react';
import LedgerApi from '../Api/api';
import LoadingSpinner from '../common/LoadingSpinner';
import UserContext from '../auth/UserContext';
import { useNavigate } from "react-router-dom";


function Profile(){
    const history = useNavigate();
    const {currentUser, setCurrentUser} = useContext(UserContext);

    const currentUsername = currentUser.user.username;

    function handleSubmit (evt){
        evt.preventDefault();
        history(`/${currentUser.user.username}/edit`);
    }

    return (
        <div>
            <div>
            Username:{currentUser.user.username}
            </div>
            <div>
            First Name:{currentUser.user.first_name}
            </div>
            <div>
            Last Name:{currentUser.user.last_name}
            </div>
            <div>
            Email:{currentUser.user.email}
            </div>
            <button onSubmit={handleSubmit}>Edit</button>
        </div>
    )

}

export default Profile;
